detaileddescription.para:
Shows how to iterate over the match results to get the captured groups/substrings. 
#include<iostream>
#include"jpcre2.hpp"


intmain(){

jpcre2::VecNumvec_num0;//VectortostorenumberedsubstringMaps.
jpcre2::VecNasvec_nas0;//VectortostorenamedsubstringMaps.
jpcre2::VecNtNvec_nn0;//VectortostoreNamedsubstringtoNumberMaps.

jpcre2::Regexre;//It'snotsupposedtothrowanyexception.

//Compilethepattern
try{re.setPattern("(?:(?<word>[?.#@:]+)|(?<word>\\w+))\\s*(?<digit>\\d+)")//setpattern
.setModifier("nJ")//setmodifier
.addJpcre2Option(jpcre2::VALIDATE_MODIFIER//validationcheck(won'thaveanyeffect)
|jpcre2::JIT_COMPILE//performJITcompile(warningifJITisnotavailable)
|jpcre2::ERROR_ALL)//treatwarningsaserrors
.addPcre2Option(0)//addpcre2option
.compile();}//Finallycompileit.
catch(jpcre2::Except&e){std::cerr<<e.getErrorMessage();}

//Theabove`jpcre2::VALIDATE_MODIFIER`optionwon'thaveanyeffectasmodifierwaspassedbeforeit.
//Youcanpassamodifier(~or&)toturnthisvalidationcheckon.Inthatcase
//validationwillstartafter~or&modifierisencountered,

//Inabove,JITcompilerwarningwillbetreatedaserrorandyouwillgetanexception.
//JITcompileerroristreatedaswarningbydefault,becauseitposesnoproblemdoingmatch
//orreplace.It'sjustanoptimization.Itwilldoit'sjob(optimizethecompiledregexforfasteroperation)
//wheneveravailableanddonothingifnot.


/***************************************************************************************************************
*Alljpcre2exceptionsareoftypejpcre2::Except
**************************************************************************************************************/

//subjectstring
std::stringsubject="(Iamastringwithwordsanddigits45andspecialschars:?.#@443অআকখগঘ56)";

size_tcount=0;

try{count=re.initMatch()//InvoketheinitMatch()function
.addModifier("~g")//setvariousparameters(~:jpcre2::ERROR_ALL)
.setSubject(subject)//...
.setNumberedSubstringVector(&vec_num0)//...
.setNamedSubstringVector(&vec_nas0)//...
.setNameToNumberMapVector(&vec_nn0)//...
.addPcre2Option(0)//...
.match();}//Finallyperformthematch
catch(jpcre2::Except&e){std::cerr<<"\n"<<e.getErrorMessage();}


//re.reset();//re-initializere


std::cout<<"\nTotalnumberofmathces:"<<count<<std::endl;
//Nowlet'saccessthematcheddata

//Eachofthesevectorscontainsmaps.
//Eachelementinthevectorspecifiesaparticularmatch
//Firstmatchisthevectorelement0,secondisatindex1andsoforth
//Amapforavectorelement,i.eforamatchcontainsallofitssubstrings/capturegroups
//Thefirstelementofthemapiscapturegroup0i.etotalmatch


for(size_ti=0;i<vec_num0.size();++i){


std::cout<<"\n##################Matchno:"<<i+1<<"####################\n";



//Thisvectorcontainsmapswithnumberasthekeyandthecorrespondingsubstringasthevalue
std::cout<<"\n-------------------------------------------------------------------------";
std::cout<<"\n---NumberedSubstrings(number:substring)formatch"<<i+1<<"---\n";
for(jpcre2::MapNum::iteratorent=vec_num0[i].begin();ent!=vec_num0[i].end();++ent){
std::cout<<"\n\t"<<ent->first<<":"<<ent->second<<"\n";
}



//Thisvectorcontainsmapswithnameasthekeyandthecorrespondingsubstringasthevalue
std::cout<<"\n-------------------------------------------------------------------------";
std::cout<<"\n---NamedSubstrings(name:substring)formatch"<<i+1<<"---\n";
for(jpcre2::MapNas::iteratorent=vec_nas0[i].begin();ent!=vec_nas0[i].end();++ent){
std::cout<<"\n\t"<<ent->first<<":"<<ent->second<<"\n";
}



//Thisvectorcontainsmapswithnameasthekeyandnumberasthevalue
//i.ethenumber(ofsubstring)canbeaccessedwiththenamefornamedsubstring.
std::cout<<"\n-------------------------------------------------------------------------";
std::cout<<"\n---Nametonumbermapping(name:number/position)formatch"<<i+1<<"---\n";
for(jpcre2::MapNtN::iteratorent=vec_nn0[i].begin();ent!=vec_nn0[i].end();++ent){
std::cout<<"\n\t"<<ent->first<<":"<<ent->second<<"\n";
}
}
return0;
}

